mapper.insert:
INSERT INTO cc (
	ccg_seq
	,name
	,oder
	,useNy
	,delNy
)
VALUE(
	#{ccg_seq}
	,#{name}
	,#{oder}
	,#{useNy}
	,#{delNy}
)

	SELECT last_insert_id()

mapper.update:

UPDATE cc
SET
	seq = #{seq}
	,ccg_seq = #{ccg_seq}
	,name = #{name}
	,oder = #{oder}
	,useNy = #{useNy}
	,delNy = #{delNy}
WHERE 1=1
	AND seq = #{seq}
