mapper.insert:

INSERT INTO cc (
	name
	,oder
	,useNy
	,delNy
	,ccg_seq
)
VALUE(
	#{name}
	,#{oder}
	,#{useNy}
	,#{delNy}
	,#{ccg_seq}
)

	SELECT last_insert_id()

mapper.update:

UPDATE cc
SET
	name = #{name}
	,oder = #{oder}
	,useNy = #{useNy}
	,delNy = #{delNy}
	,ccg_seq = #{ccg_seq}
WHERE 1=1
	AND seq = #{seq}
