mapper.sql:
FROM ccg a WHERE 1=1 AND delNy = #{shDelNy}
AND seq = #{shValue} AND name LIKE CONCAT('%',#{shValue},'%')
AND useNy LIKE CONCAT ('%',0,'%') AND useNy LIKE CONCAT ('%',1,'%')

mapper.insert:

INSERT INTO ccg (
	name
	,useNy
	,oder	
	,delNy
)
VALUE (
	#{name}
	,#{useNy}
	,#{oder}
	,#{delNy}
)

	SELECT last_insert_id()

mapper.update:


UPDATE ccg 
SET
	name = #{name}
	,useNy = #{useNy}
	,oder = #{oder}
	,delNy = #{delNy}

WHERE 1=1
	AND seq = #{seq}
