mapper.insert:
INSERT INTO member (
	user_type
	,id
	,password
	,name
	,dob
	,gender
	,email
	,email_domain
	,telecom
	,phone_number
	,director_type
	,actor_picture
	,actor_height
	,actor_weight
	,actor_eyelid
	,actor_voice
	,age
	,company_useNy
	,delNy
	,creDate
	,modDate
	,adminNy
	,actor_specialty
	,aboutMe
)
VALUES (
	#{user_type}
	,#{id}
	,#{password}
	,#{name}
	,#{dob}
	,#{gender}
	,#{email}
	,#{email_domain}
	,#{telecom}
	,#{phone_number}
	,#{director_type}
	,#{actor_picture}
	,#{actor_height}
	,#{actor_weight}
	,#{actor_eyelid}
	,#{actor_voice}
	,#{age}
	,#{company_useNy}
	,#{delNy}
	,#{creDate}
	,#{modDate}
	,#{adminNy}
	,#{actor_specialty}
	,#{aboutMe}
)

	SELECT last_insert_id()

INSERT INTO company (
	name_type
	,tel
	,zip_code
	,address
	,address_detail
	,address_Lat
	,address_Lng
	,member_seq
)
VALUES (
	#{company_name_type}
	,#{company_tel}
	,#{company_zip_code}
	,#{company_address}
	,#{company_address_detail}
	,#{company_Lat}
	,#{company_Lng}
	,#{seq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE member
SET
	user_type 			= #{user_type}
	,id 				= #{id}
	,password 			= #{password}
	,name 				= #{name}
	,dob 				= #{dob}
	,gender 			= #{gender}
	,email 				= #{email}
	,email_domain 		= #{email_domain}
	,telecom 			= #{telecom}
	,phone_number 		= #{phone_number}
	,director_type 		= #{director_type}
	,actor_picture 		= #{actor_picture}
	,actor_height 		= #{actor_height}
	,actor_weight 		= #{actor_weight}
	,actor_eyelid 		= #{actor_eyelid}
	,actor_voice 		= #{actor_voice}
	,age 				= #{age}
	,company_useNy 		= #{company_useNy}
	,delNy 				= #{delNy}
	,creDate 			= #{creDate}
	,modDate 			= #{modDate}
	,adminNy 			= #{adminNy}
	,actor_specialty 	= #{actor_specialty}
	,aboutMe 			= #{aboutMe}
WHERE 1=1
	AND seq = #{seq}
