mapper.insert:
INSERT INTO member (
	user_type
	,id
	,password
	,name
	,dob
	,gender
	,email
	,email_domain
	,phone_number
	,director_type
	,actor_picture
	,actor_height
	,actor_weight
	,actor_eyelid
	,actor_voice
	,age
	,company_useNy
	,zip_code
	,address
	,address_detail
	,address_Lat
	,address_Lng
	,telecom

)
VALUES (
	#{user_type}
	,#{id}
	,#{password}
	,#{name}
	,#{dob}
	,#{gender}
	,#{email}
	,#{email_domain}
	,#{phone_number}
	,#{director_type}
	,#{actor_picture}
	,#{actor_height}
	,#{actor_weight}
	,#{actor_eyelid}
	,#{actor_voice}
	,#{age}
	,#{company_useNy}
	,#{zip_code}
	,#{address}
	,#{address_detail}
	,#{address_Lat}
	,#{address_Lng}
	,#{telecom}
)

	SELECT last_insert_id()

mapper.update:

UPDATE member
SET
	user_type = #{user_type}
	,id = #{id}
	,password = #{password}
	,name = #{name}
	,dob = #{dob}
	,gender = #{gender}
	,email = #{email}
	,phone_number = #{phone_number}
	,director_type = #{director_type}
	,actor_picture = #{actor_picture}
	,actor_height = #{actor_height}
	,actor_weight = #{actor_weight}
	,actor_eyelid = #{actor_eyelid}
	,actor_voice = #{actor_voice}
	,age = #{age}
	,company_useNy = #{company_useNy}
	,zip_code = #{zip_code}
	,address = #{address}
	,address_detail = #{address_detail}
WHERE 1=1
	AND seq = #{seq}
