mapper.sql:
FROM member a WHERE 1=1 AND delNy = #{shDelNy}
AND seq = #{shValue} AND name LIKE CONCAT('%',#{shValue},'%') AND user_type LIKE CONCAT('%',#{shValue},'%')
AND delNy LIKE CONCAT ('%',0,'%') AND delNy LIKE CONCAT ('%',1,'%')

mapper.insert:
INSERT INTO member (
	user_type
	,id
	,password
	,name
	,dob
	,gender
	,email
	,email_domain
	,telecom
	,phone_number
	,director_type
	,actor_height
	,actor_weight
	,actor_eyelid
	,actor_voice
	,age
	,company_useNy
	,creDate
	,actor_specialty
	,aboutMe
)
VALUES (
	#{user_type}
	,#{id}
	,#{password}
	,#{name}
	,#{dob}
	,#{gender}
	,#{email}
	,#{email_domain}
	,#{telecom}
	,#{phone_number}
	,#{director_type}
	,#{actor_height}
	,#{actor_weight}
	,#{actor_eyelid}
	,#{actor_voice}
	,#{age}
	,#{company_useNy}
	,now()
	,#{actor_specialty}
	,#{aboutMe}
)

	SELECT last_insert_id()

