mapper.update:
UPDATE member
SET
	user_type 			= #{user_type}
	,id 				= #{id}
	,password 			= #{password}
	,name 				= #{name}
	,dob 				= #{dob}
	,gender 			= #{gender}
	,email 				= #{email}
	,email_domain 		= #{email_domain}
	,telecom 			= #{telecom}
	,phone_number 		= #{phone_number}
	,director_type 		= #{director_type}
	,actor_height 		= #{actor_height}
	,actor_weight 		= #{actor_weight}
	,actor_eyelid 		= #{actor_eyelid}
	,actor_voice 		= #{actor_voice}
	,age 				= #{age}
	,company_useNy 		= #{company_useNy}
	,modDate 			= now()
	,actor_specialty 	= #{actor_specialty}
	,aboutMe 			= #{aboutMe}
WHERE 1=1
	AND seq = #{seq}

UPDATE actor_sns
SET
	type		   = #{Upsns_type}
	,url		   = #{Upurl}
WHERE 1=1
	AND snsSeq = #{snsSeq}

UPDATE actor_filmography
SET
	period		= #{Upfilmo_period}
	,type		= #{Upfilmo_type}
	,producer	= #{Upfilmo_producer}
	,name		= #{Upfilmo_name}
	,role		= #{Upfilmo_role}
WHERE 1=1
	AND filmoSeq = #{filmoSeq}

UPDATE actor_education
SET
	period_s		= #{Upedu_period_s}
	,period_e		= #{Upedu_period_e}
	,school_name	= #{Upschool_name}
	,major			= #{Upedu_major}
	,type			= #{Upedu_type}
WHERE 1=1
	AND eduSeq = #{eduSeq}

UPDATE actor_award
SET
	period		= #{Upaward_period}
	,name		= #{Upaward_name}
	,issuer		= #{Upaward_issuer}
WHERE 1=1
	AND awardSeq = #{awardSeq}

UPDATE memberUploaded
SET	
	type			= #{type}
	,defaultNy		= #{defaultNy}
	,sort			= #{sort}
	,path			= #{path}
	,originalName	= #{originalName}
	,uuidName		= #{uuidName}
	,ext			= #{ext}
	,size			= #{size}
	,delNy			= 0
WHERE 1=1
AND pseq = #{seq}
AND fileSeq = #{fileSeq}
