mapper.select:
SELECT a.seq ,a.member_seq ,a.type ,a.name ,a.age ,a.gender ,a.casting ,a.producer ,a.director ,a.location ,a.deadline ,a.manager ,a.pay ,a.email ,a.contents ,b.name AS writer
AND a.type = #{type}
ORDER BY a.seq DESC

mapper.insert:
INSERT INTO post_odition (
	type
	,member_seq	
	,name		
	,age	
	,gender	 
	,casting 
	,producer	 
	,director	 
	,location	 
	,deadline
	,manager	 
	,pay		 
	,email		 
	,contents
	,zip_code
	,address
	,address_detail	
)
VALUE (
	#{type}
	,#{member_seq}
	,#{name}
	,#{age}
	,#{gender}
	,#{casting}
	,#{producer}
	,#{director}
	,#{location}
	,#{deadline}
	,#{manager}
	,#{pay}
	,#{email}
	,#{contents}
	,#{zip_code}
	,#{address}
	,#{address_detail}
)	

		SELECT last_insert_id()

INSERT INTO oditionSupport (
	odition_seq
	,mm_seq
)
VALUE (
	#{post_odition_seq}
	,#{member_seq}
)

	SELECT last_insert_id()

mapper.delete:

DELETE 
FROM oditionSupport
WHERE odition_seq = #{odition_seq}
AND mm_seq = #{member_seq}

mapper.select:
SELECT
	COUNT(*)
FROM oditionSupport 
WHERE 1=1
AND odition_seq = #{post_odition_seq}
AND mm_seq = #{member_seq}
